import React from 'react';
import Location from './Location';
import Error from './Error';
import axios from 'axios';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      city: '',
      locationData: [],
      error: true
    };
  }

  handleCityInput = (event) => {
    this.setState({
      city: event.target.value
    });
  };

  handleGetCityInfo = async (event) => {
    event.preventDefault();
    let url = `https://us1.locationiq.com/v1/search?key=${process.env.REACT_APP_KEY_API}&q=${this.state.city}&format=json`;

    try {
      const response = await axios.get(url);
      const cityDataFromAxios = response.data;
      console.log(cityDataFromAxios.d);
      // Update the locationData state with the retrieved data
      this.setState({
        locationData: cityDataFromAxios,
        error: false
      });
    } catch (error) {
      console.error(error);
      this.setState({
        error: true
      });
    }
  };

  render() {
    return (
      <>
        <form onSubmit={this.handleGetCityInfo}>
          <label htmlFor=""> Enter a City Name: 
            <input type="text" onInput={this.handleCityInput}/>
          </label>
          <button type="submit">Explore!</button>
        </form>

        {/* SHORT CIRCUIT - &&  */}
        {this.state.error && <Error />}
      </>
    );
  }
}

export default App;
